/** @jsx element */

import {expect} from 'chai';

import {element} from 'deku';

import {Tab,TabItem} from '../../lib/tab/deku/';

describe('[Tab]',()=>{
	it('should work',()=>{
		expect(()=>{
			<Tab name="test">
			</Tab>
		}).not.to.throw();
	});
});

describe('[TabItem]',()=>{
	it('should work',()=>{
		expect(()=>{
			<Tab>
				<TabItem name="foo" group="test" title="Foo">
					<p>foooo</p>
				</TabItem>
				<TabItem name="bar" group="test" title="Foo" default>
					<p>bar</p>
				</TabItem>
			</Tab>
		}).not.to.throw();
	});
});
