const Type = require('../union-type');

const Point = Type({Point: [Number, Number]});

const projectX = Point.case({
  Point: (x, y) => Point.Point(0, y),
});

const p = Point.Point(12, 14);
const p2 = projectX(p);

let [x, y] = p;

console.log(x, y, p2);

var Tree = (a) => Type({Empty: [], Leaf: [a, Tree(a), Tree(a)]});
